export default {
  name: 'VaColorPicker',
  props: {
    color: {
      type: String,
      default: '#ffff00',
      required: false
    },
    prefixCls: {
      type: String,
      default: 'va'
    }
  },
  data () {
    return {
      show: false,
      position: {}
    }
  },
  mounted () {
    this.getPosition()
  },
  methods: {
    getPosition () {
      let rect = this.$refs.colorpicker.$el.getBoundingClientRect()
      this.position = {
        top: rect.top,
        right: rect.right,
        bottom: rect.bottom,
        left: rect.left,
        width: rect.width,
        height: rect.height,
        x: rect.x,
        y: rect.y
      }
    },
    toggleColorPicker () {
      this.getPosition()
      this.show = !this.show
    }
  },
  computed: {
    classObj () {
      let {prefixCls} = this
      let klass = {}

      klass[prefixCls + '-color-picker'] = true

      return klass
    }
  }
}
